    "results": [
        {
            "number": 1729,
            "expression1": "1^3 + 12^3",
            "expression2": "9^3 + 10^3"
        },
        {
            "number": 4104,
            "expression1": "2^3 + 16^3",
            "expression2": "9^3 + 15^3"
        },
        {
            "number": 13832,
            "expression1": "2^3 + 24^3",
            "expression2": "18^3 + 20^3"
