        cerr << "Unable to open file datafile.txt";
        exit(1);
    }
    vector<FamilyMember> familyMembers;
    string line;
    while (getline(inFile, line)) {
        string name, relation;
        int age;
        // extract data from line and store it in variables
        // ...
        familyMembers.push_back({name, relation, age});
    }
    // perform analysis on the data
    // ...
    inFile.close();
    return 0;
}
