```csharp
double fileSize = 1024; // in KB
double downloadTime = 10; // in seconds
double uploadTime = 20; // in seconds
double downloadSpeed = fileSize / downloadTime;
double uploadSpeed = fileSize / uploadTime;
Console.WriteLine("My download speed is: " + downloadSpeed + " KB/s");
Console.WriteLine("My upload speed is: " + uploadSpeed + " KB/s");
double neighborDownloadSpeed = 12.5; // in KB/s
double neighborUploadSpeed = 7.8; // in KB/s
if (downloadSpeed > neighborDownloadSpeed) {
    Console.WriteLine("My download speed is faster than my neighbor's!");
} else if (downloadSpeed < neighborDownloadSpeed) {
    Console.WriteLine("My neighbor's download speed is faster than mine!");
